import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Signups } from 'src/assets/signups.model';


@Injectable({
  providedIn: 'root'
})
export class UserapiService implements OnInit {

  header:any;

  constructor(private httpClient:HttpClient) { }

  ngOnInit(): void {
     this.header = {
      headers: new HttpHeaders()
     .set('Authorization',  `Bearer ${localStorage.getItem('Authorization')}`)
   }
  }

  usersData(){   
 return this.httpClient.get<Signups[]>("http://localhost:8080/v2/signups",this.header);
  }



deleteUser(sid:number){
  return this.httpClient.delete(this.url+sid, this.header);
}



}
