import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { UserapiService } from '../services/userapi.service';
import { Signups } from 'src/assets/signups.model';

@Component({
  selector: 'app-edituser',
  templateUrl: './edituser.component.html',
  styleUrls: ['./edituser.component.css']
})
export class EdituserComponent implements OnInit{

  sid:any
  edituser:any
  username:string=""
  constructor(private router: ActivatedRoute,private userService:UserapiService){  }


  ngOnInit(): void {

    this.router.paramMap.subscribe(params => {      
      this.sid = params.get('sid');
    })

     this.userService.getUser(this.sid).subscribe((data:any)=>{
      console.log(data);  
      this.edituser=data;
      console.log(this.username);

  });

  }

}
<p>edituser works!</p>

<div class="container">
    <br/><br/>
    <h3>Update User Page!</h3>
     <br/>
     <img src="http://www.checkbilliard.com/wp-content/uploads/2015/07/update_icon.jpg" style="height: 180px;">
     <hr/>
     <span style="color:red;font-weight: bold;"></span>
    <form>
        <div class="form-group" style="width:50%;">
        <label>Username </label>
        <input type="text" name="username" value="username" [(ngModel)]="edituser.username" class="form-control" />

     </div>
     <br/>
     <div class="form-group" style="width:50%;">
       <label>Password </label>
       <input  type="password" name="password" class="form-control"/>
    </div>
    <br/>
    <div class="form-group" style="width:50%;">
        <label>Email </label>
        <input  type="email" name="email" class="form-control"/>
     </div>
     <br/>
     <div class="form-group" style="width:50%;">
        <label>Mobile </label>
        <input  type="number" name="mobile" class="form-control"/>
     </div>
     <br/>
     <div class="form-group" style="width:50%;">
        <label>Gender</label>
        <select name="gender" class="form-control">
                <option>Male</option>
                <option>Female</option>
            </select>
     </div>

    <div class="form-group" style="width:50%;">

    <hr/>
     <button  type="button" name="login" class="btn btn-primary">
       Summit
      </button>
      &nbsp; &nbsp; 
      <button  type="button" name="cancel" class="btn btn-danger" routerLink="/home">
        Cancel
       </button> 
  </div>
   </form> 

 </div>
